import { Side } from "./enum";
import { TriangleType } from "./enum";
import { TriangleShape } from "./Triangle";

export default class TriangleModel {
    private triangle: TriangleShape;

    constructor() {
        this.triangle = new TriangleShape(0, 0, 0);
    }

    getLocale(type: TriangleType) {
        switch (type) {
            case TriangleType.EQUILATERAL:
                return "Triangle is equilateral";
            case TriangleType.SCALENE:
                return "Triangle is scalene";
            case TriangleType.ISOSCELES:
                return "Triangle is isosceles";
        }
        throw new Error(`Trigangle type ${type} locale is not defined`);
    }

    getTriangleType() {
        const type = this.triangle.getType();
        if (type === TriangleType.INVALID) {
            return "Invalid triangle sides";
        }

        return this.getLocale(type);
    }

    setSide(side: Side, value: number) {
        if (side == Side.sideA) {
            this.triangle.a = value;
        } else if (side == Side.sideB) {
            this.triangle.b = value;
        } else if (side == Side.sideC) {
            this.triangle.c = value;
        }
    }
}
